opam.opam:
opam-version: "2.0"
name: "ppx_tools_versioned"
version: "5.2.1"
synopsis: "A variant of ppx_tools based on ocaml-migrate-parsetree"
maintainer: "frederic.bour@lakaban.net"
authors: [
  "Frédéric Bour <frederic.bour@lakaban.net>"
  "Alain Frisch <alain.frisch@lexifi.com>"
]
license: "MIT"
tags: "syntax"
homepage: "https://github.com/let-def/ppx_tools_versioned"
bug-reports: "https://github.com/let-def/ppx_tools_versioned/issues"
depends: [
  "ocaml" {>= "4.02.0"}
  "jbuilder" {build & >= "1.0+beta17"}
  "ocaml-migrate-parsetree" {>= "1.0.10"}
]
build: [
  ["jbuilder" "subst" "-p" name] {pinned}
  ["jbuilder" "build" "-p" name "-j" jobs]
  ["jbuilder" "runtest" "-p" name "-j" jobs] {with-test}
]
dev-repo: "git://github.com/let-def/ppx_tools_versioned.git"
url {
  src:
    "https://github.com/ocaml-ppx/ppx_tools_versioned/archive/5.2.1.tar.gz"
  checksum: "md5=1ae6ae43ec161fbbf12c2b4d3a7e26f5"
}

files.content:
open Printf

let () =
  let dir = Sys.argv.(1) in
  (try Sys.chdir dir
   with _ -> eprintf "Cannot change directory to %s\n%!" dir);
  exit (Sys.command "ocaml setup.ml -uninstall")

opam.opam:
opam-version: "2.0"
name: "ocamlmod"
version: "0.0.9"
synopsis: "Generate OCaml modules from source files"
maintainer: "opam-devel@lists.ocaml.org"
authors: "Sylvain Le Gall"
license: "LGPL-2.1 with OCaml linking exception"
homepage: "https://forge.ocamlcore.org/projects/ocamlmod/"
bug-reports: "https://forge.ocamlcore.org/tracker/?group_id=244"
depends: [
  "ocaml"
  "ocamlfind" {build}
  "ounit" {with-test & >= "2.0.0"}
  "ocamlbuild" {build}
]
build: [
  ["ocaml" "setup.ml" "-configure" "--prefix" prefix]
  ["ocaml" "setup.ml" "-build"]
  ["ocaml" "setup.ml" "-configure" "--enable-tests"] {with-test}
  ["ocaml" "setup.ml" "-build"] {with-test}
  ["ocaml" "setup.ml" "-test"] {with-test}
]
install: ["ocaml" "setup.ml" "-install"]
remove: ["ocaml" "%{etc}%/ocamlmod/_oasis_remove_.ml" "%{etc}%/ocamlmod"]
dev-repo:
  "darcs+ssh://https//forge.ocamlcore.org/anonscm/darcs/ocamlmod/ocamlmod"
extra-files: [
  ["ocamlmod.install" "md5=0d1b822c897681cf54b3e1aed52dda99"]
  ["_oasis_remove_.ml" "md5=6100ca146fa97d2196eb49a2631d0796"]
]
url {
  src:
    "https://forge.ocamlcore.org/frs/download.php/1702/ocamlmod-0.0.9.tar.gz"
  checksum: "md5=b52bfbab6bb77f9736bde9c2fe81c508"
}

